mapper.insert:
INSERT INTO message
	VALUES( message_seq.nextVal
			, #{userID}
			, #{userName}
			, #{message}
			, sysdate
			, #{projectName})

mapper.select:
	SELECT userID, userName, message, to_char(messageDate, 'YY-MM-DD HH24:MI:SS') messageDate
	FROM message
	WHERE projectName = #{projectName}
 	AND
			 to_char(messageDate, 'YYYY-MM-DD') = #{date}
	order by messageSeq desc
